const theDate = new Date();
console.log(theDate)

export function getYear() {
    var year = theDate.getFullYear();
    return year;
}

function isLeapYear() {
    return ((getYear() % 4 === 0) && (getYear() % 100 !== 0)) || (getYear() % 400 === 0);
}

function getMonthNum() {
    var month = theDate.getMonth();
    console.log(month)
    return month;
}

function getDay() {
    var day = theDate.getDate();
    return day;
}

export function getWeekDay() {
    var weekDay = theDate.toString().slice(0,3);
    // console.log("The week day is: " + weekDay);
    return weekDay;
}

export function getMonth() {
    var monthNum = getMonthNum();
    if(monthNum === 0) {
        return "January";
    } else if(monthNum === 1) {
        return "February";
    } else if(monthNum === 2) {
        return "March";
    } else if(monthNum === 3) {
        return "April";
    } else if(monthNum === 4) {
        return "May";
    } else if(monthNum === 5) {
        return "June";
    } else if(monthNum === 6) {
        return "July";
    } else if(monthNum === 7) {
        return "August";
    } else if(monthNum === 8) {
        return "September";
    } else if(monthNum === 9) {
        return "October";
    } else if(monthNum === 10) {
        return "November";
    }else {
        return "December";
    }
}

function getDaysArray() {
    console.log(isLeapYear());
}
