function getDate() {
    var date = new Date();
    var dateString = date.toString();

    return dateString;

    // var weekDay = dateString.slice(0,3);
    // var month = dateString.slice(4,7);
    // var day = dateString.slice(8,10);
    // console.log(weekDay);
    // console.log(month);
    // console.log(day);
    // console.log(year);
};

export function getYear() {
    var year = getDate().slice(11,15);
    return year;
}

export function getMonth() {
    var month = getDate().slice(4,7);
    return month;
}

export function getDay() {
    var day = getDate().slice(8,10);
    return day;
}

export function getWeekDay() {
    var weekDay = getDate().slice(0,3);
}
